var tesseract = variables.tesseract;

var timeSelector = "quarterOption"; // yearOption, quarterOption
var timeSelectorName = "Quarterly";
var time = timeSelector === "yearOption" ? "Year" : "Quarter";
var timeId = timeSelector === "yearOption" ? "Year" : "Quarter ID";

var activities = "totalOption"; //totalOption, byActivity
var activityQuery = activities === "totalOption" ?  "" : "Economic Activity Section,";

return {
  data: (tesseract + "/data.jsonrecords?cube=gastat_gdp&drilldowns=" + activityQuery + time + "&measures=GDP"),
  dataFormat: function (resp) {
    var data = resp.data.map(function (d) { return Object.assign.apply(Object, [ {} ].concat( d )); });

    data.forEach(function (d) {
      d.Nation = "Saudi Arabia";
      d.GDP = d.GDP * 1000000;
    });

    return data;
  },
  groupBy: activities === "totalOption" ? "Nation" : "Economic Activity Section",
  legendPosition: "bottom",
  legendTooltip: {
    tbody: false,
  },
  lineLabels: true,
  shapeConfig: {
    Line: {
      label: function label(d) {
        var dataArray = this._filteredData
          	.filter(function (f) { return f.Nation === d.Nation; });
        var maxX = Math.max.apply(Math, dataArray.map(function (f) { return f[timeId]; }));			
        var maxData = dataArray
        	.find(function (f) { return f[timeId] === maxX; });

        return ("" + (formatters.sar(maxData.GDP)));
      },
    }
  },
  title: activities === "totalOption" ? ("Evolution of the Gross Domestic Product<br /> (" + timeSelectorName + ")")
                                      : ("Evolution of the Gross Domestic Product by Economic Activity <br /> (" + timeSelectorName + ")"),
  time: time,
  type: activities === "totalOption" ? "LinePlot" : "StackedArea",
  tooltip: true,
  tooltipConfig: {
    tbody: [
      [time, function (d) { return d[time]; }],
      ["GDP", function (d) { return formatters.sar(d["GDP"]); }]
    ]
  },
  x: time,
  xConfig: {
    title: false
  },
  y: "GDP",
  yConfig: {
    title: false,
    tickFormat: function (d) { return formatters.sar(d); }
  }
};

return {
  data: ((variables.tesseract) + "/data.jsonrecords?cube=sama_inflation&drilldowns=Month,Main+Division&measures=Inflation"),
