      ["Month", function (d) { return d["Month"]; }],
      ["Consumer Price Index", function (d) { return formatters.abbreviate(d["Inflation"]); }]
    ]
  },
  type: "BarChart",
  time: "Month",
  timeline: true,
  x: "Inflation",
  xConfig: {
    title: "Consumer Price Index (2018=100)",
    tickFormat: function (d) { return ("" + ((formatters.abbreviate(d)))); },
  },
  ySort: function (a, b) { return a["Inflation"] - b["Inflation"]; },
  y: "Main Division",
  yConfig: {
    title: false
  }
};

var dataURL = (variables.tesseract) + "/data.jsonrecords?cube=gastat_ipi_economic_activity&drilldowns=Month,Category&measures=Percentage+change+IPI"

return {
  data: dataURL,
  dataFormat: function (resp) {
    var data_ = resp.data.map(function (d) { return Object.assign({}, d); });
      data_.forEach(function (d) {
        d["Group"] = d["Category ID"] === 4 ? "Bar" : "Line"
       // const [year, month] = d["Month"].split("-");
      //  d["Time"] = new Date(year, month*1).getTime();
    });
  return data_.filter(function (d) { return d["Percentage change IPI"]; })
  },
  discrete: "x",
  height: 500,
  groupBy: ["Category"],
  shape: function (d) { return d.Group; },
  legendPosition: "bottom",
  legendTooltip: {
    tbody: false,
  },
  stacked: true,
  time: "Month",
  title: "Percentage Change in the Industrial Production Index by Economic Activity",
  tooltipConfig: {
    tbody: [
      ["Month", function (d) { return d["Month ID"]; }],
      ["Industrial Production Index Change", function (d) { return ((formatters.abbreviate(d["Percentage change IPI"])) + "%"); }]
    ]
  },
  type: "BarChart",
  x: "Month",
  xConfig: {
    title: "",
  },
  y: "Percentage change IPI",
  yConfig: {
    title: "Percentage Change",
    tickFormat: function (d) { return ((formatters.abbreviate(d)) + "%"); },
  },
}

var abbreviate = formatters.abbreviate;
var commas = formatters.commas;
var growthPct = formatters.growthPct;
var ref = libs.d3;
var nest = ref.nest;
var sum = ref.sum;
var selector = "totalOption" //totalOption, shareOption
var measure = selector === "totalOption" ? "Economic Establishments" : "Percentage Value"

return {
  data: ((variables.tesseract) + "/data.jsonrecords?cube=gastat_economic_establishments&drilldowns=Economic+Activity+Section,Size+Class,Year&measures=Economic+Establishments"),

  dataFormat: function (resp) {

    nest()
      .key(function (d) { return ((d.Year) + "_" + (d["Economic Activity Section ID"])); })
      .entries(resp.data)
      .forEach(function (group) {
        var total = sum(group.values, function (d) { return d["Economic Establishments"]; });
        group.values
          .forEach(function (d) {
            d["Percentage Value"] = (d["Economic Establishments"] / total) * 100
          });
      });
    return resp.data.filter(function (d) { return d["Economic Establishments"]; });
  },

  discrete: "y",
  groupBy: ["Size Class ID"],
  height: 500,
  label: function (d) { return ""; },
  legendConfig: {
    label: function (d) { return ((d["Size Class"]) + " employees"); }
  },
  legendPosition: "bottom",
  legendTooltip: {
    tbody: false,
  },
  stacked: true,
  time: "Year",
  timeline:true,
  tooltipConfig: {
    title: function (d) { return ((d["Size Class"]) + " employees"); },
    tbody: [
      ["Year", function (d) { return d["Year"]; }],
      ["Economic Activity Section", function (d) { return d["Economic Activity Section"]; }],
      ["Economic Establishments", function (d) { return commas(d["Economic Establishments"]); }],
      ["Share", function (d) { return ((abbreviate(d["Percentage Value"])) + "%"); }] ]
  },
  type: "BarChart",
  x: measure,
  xConfig: {
    tickFormat: selector === "totalOption" ? function (d) { return abbreviate(d); } : function (d) { return ((abbreviate(d)) + "%"); },
    title: "",
  },
  y: "Economic Activity Section",
  yConfig: {
    title: "",
  },
  ySort: function (b, a) { return a["Economic Activity Section"].localeCompare(b["Economic Activity Section"]); }
};

var abs = formatters.abs;

