var exports = (variables.tesseract) + "/data.jsonrecords?cube=gastat_trade_balance&drilldowns=Month&measures=Exports"
var imports = (variables.tesseract) + "/data.jsonrecords?cube=gastat_trade_balance&drilldowns=Month&measures=Imports"
var tradeBalance = (variables.tesseract) + "/data.jsonrecords?cube=gastat_trade_balance&drilldowns=Month&measures=Trade+Balance"


return {
  data: [exports, imports, tradeBalance],
  dataFormat: function (resp) {
    var exports_ = resp[0].data;
    var imports_ = resp[1].data;
    var tradeBalance_ = resp[2].data;

    var updateData = function (data, flow, flowID, value) {
      data.forEach(function (d) {
        d["Flow ID"] = flowID;
        d["Flow"] = flow;
        d["Value"] = d[value]*1000000
        delete d[value];
      });
      return data;
    };

    exports_ = updateData(exports_, "Export", 1, "Exports");
    imports_ = updateData(imports_, "Import", 2, "Imports");
    tradeBalance_ = updateData(tradeBalance_, "Trade Balance ", 3, "Trade Balance");

    return exports_.concat(imports_.concat( tradeBalance_));
  },
  groupBy: ["Flow ID"],
  label: function (d) { return d["Flow"]; },
  legendPosition: "bottom",
  legendTooltip: {
    tbody: false,
  },
  lineLabels: true,
  shapeConfig: {
    Line: {
      label: function label(d) {
        var dataArray = this._filteredData
          	.filter(function (f) { return f["Flow ID"] === d["Flow ID"]; });
        var maxX = Math.max.apply(Math, dataArray.map(function (f) { return f["Month ID"]; }));			
        var maxData = dataArray
        	.find(function (f) { return f["Month ID"] === maxX; });

        return ("" + (formatters.sar(maxData["Value"])));
      },
      strokeDasharray: function (d) { return d["Flow ID"]*1 === 3 ? "5 3" : "0"; }
    }
  },
  time: "Month",
  tooltipConfig: {
    tbody: [
      ["Month", function (d) { return d["Month"]; }],
      ["Merchandise Value", function (d) { return formatters.sar(d["Value"]); }]
    ]
  },
  title: "Merchandise Exports and Imports",
  type: "LinePlot",
  x: "Month",
  xConfig: {
    title: ""
  },
  y: "Value",
  yConfig: {
    title: "",
    tickFormat: function (d) { return formatters.sar(d); }
  }
};

