const imports = `${variables.tesseract}/data.jsonrecords?cube=gastat_trade_balance&drilldowns=Month&measures=Imports`
const tradeBalance = `${variables.tesseract}/data.jsonrecords?cube=gastat_trade_balance&drilldowns=Month&measures=Trade+Balance`


return {
  data: [exports, imports, tradeBalance],
  dataFormat: resp => {
    let exports_ = resp[0].data;
    let imports_ = resp[1].data;
    let tradeBalance_ = resp[2].data;

    const updateData = (data, flow, flowID, value) => {
      data.forEach(d => {
        d["Flow ID"] = flowID;
        d["Flow"] = flow;
        d["Value"] = d[value]*1000000
        delete d[value];
      });
      return data;
    };

    exports_ = updateData(exports_, "Export", 1, "Exports");
    imports_ = updateData(imports_, "Import", 2, "Imports");
    tradeBalance_ = updateData(tradeBalance_, "Trade Balance ", 3, "Trade Balance");

    return exports_.concat([...imports_, ...tradeBalance_]);
  },
  groupBy: ["Flow ID"],
  label: d => d["Flow"],
